# Generate 50 text files, each corresponding to a fruit, with 100 unique prompts
for vegetable, characteristics in vegetable_characteristics.items():
    prompts = []
    for i in range(100):
        geometry = characteristics['geometry'][i % len(characteristics['geometry'])]
        texture = characteristics['texture'][i % len(characteristics['texture'])]
        color = characteristics['color'][i % len(characteristics['color'])]
        #prompt = f"A DSLR photo of a single unique centered {color} {vegetable.lower()} with a {texture} texture and a {geometry} shape, three-quarter view, on a plain white background with no shadow or reflection."
        prompt = f"A high-quality DSLR photo of a single, unique {color} {vegetable.lower()}, centered in the frame. The {vegetable.lower()} is standing vertically, showcasing a {texture} texture and a {geometry} shape. The image is captured from a three-quarter perspective against a plain white background, free of shadows or reflections."
        prompts.append(prompt)
    
    # Save each fruit's prompts to a txt file
    file_path = f"prompt/vegetables_final/{vegetable.lower().replace(' ', '_')}.txt"
    with open(file_path, "w") as file:
        for prompt in prompts:
            file.write(f"{prompt}\n")

# List the file paths for user access
