# Generate 50 text files, each corresponding to a fruit, with 100 unique prompts
for fruit, characteristics in expanded_fruit_characteristics.items():
    prompts = []
    print(fruit)
    if fruit!="Bananas" : 
        continue
    for i in range(100):
        geometry = characteristics['geometry'][i % len(characteristics['geometry'])]
        texture = characteristics['texture'][i % len(characteristics['texture'])]
        color = characteristics['color'][i % len(characteristics['color'])]
        #prompt = f"A DSLR photo of a single unique centered {color} {fruit.lower()} oriented to the up with a {texture} texture and a {geometry} shape, three-quarter view, on a plain white background with no shadow or reflection."
        prompt = f"A high-quality DSLR photo of a single unique alone {color} {fruit.lower()} laying on a white backgroung,  only one single {fruit.lower()} centered in the frame. The {fruit.lower()} is positioned horizontally. The {fruit.lower()} showcases a {texture} texture and a natural {geometry} shape. The image is captured from a top view on the white background, free of shadows or reflections."
        prompts.append(prompt)
    
    # Save each fruit's prompts to a txt file
    file_path = f"prompt/fruits_final/{fruit.lower().replace(' ', '_')}_test8.txt"
    with open(file_path, "w") as file:
        for prompt in prompts:
            file.write(f"{prompt}\n")

